    <script>
        function stateInvalid() {
            jQuery('.gfield:not(.validateState) input,.gfield:not(.validateState) select').prop('disabled', true);
            jQuery('.gform_footer,.gform_page_footer, .validateState .gfield_description').hide();
            jQuery('.errorState').fadeIn();
            console.log('stateInvalid');
        }

        function caseInvalid() {
            jQuery('.gform_footer,.gform_page_footer').hide();
            jQuery('.gfield:not(.validateCase) input,.gfield:not(.validateCase) select').prop('disabled', true);
            jQuery('.errorCase').fadeIn();
            console.log('caseInvalid');
        }

        function caseInvalidss() {
            jQuery('.gform_footer,.gform_page_footer').hide();
            jQuery('.gfield:not(.validateCase) input,.gfield:not(.validateCase) select').prop('disabled', true);
            jQuery('.errorCaseSocial').fadeIn();
            console.log('caseInvalidss');
        }

        function allValid() {
            jQuery('.gform_footer,.gform_page_footer').show();
            jQuery('.gfield input,.gfield select').prop('disabled', false);
            jQuery('.errorState, .errorCase, .errorCaseSocial').hide();
            console.log('allValid');
        }

        jQuery(function() {
            jQuery('.validateState .ginput_container').append('<span class="errorState">Oasis does not fund applications in your state.</span>');
            jQuery('.validateCase .ginput_container').append('<span class="errorCase">Oasis does not fund workers compensation cases in your state.</span>');
            jQuery('.validateCase .ginput_container').append('<span class="errorCaseSocial">Oasis does not fund  Social Security/Disability cases</span>');

            // Resetting on page load
            allValid();

            // no funding, period
            var states = [
                'Arkansas',
                'Kansas',
                'Kentucky',
                'Maryland',
                'Nevada',
                'North Carolina',
                'West Virginia'
            ];
            // no workmans comp & ss case states
            var noWCCases = [
                'Alabama',
                'Alaska',
                'Arizona',
                'California',
                'Colorado',
                'District of Columbia',
                'Delaware',
                'Hawaii',
                'Idaho',
                'Iowa',
                'Maine',
                'Massachusetts',
                'Michigan',
                'Minnesota',
                'Nebraska',
                'Nevada',
                'New Hampshire',
                'New Jersey',
                'New York',
                'North Dakota',
                'Mississippi',
                'Montana',
                'Oregon',
                'Pennsylvania',
                'Rhode Island',
                'South Dakota',
                'Tennessee',
                'Texas',
                'Utah',
                'Vermont',
                'Virginia',
                'Washington',
                'Wisconsin',
                'Wyoming',
            ];

            jQuery('.validateState select').on('change', function() {
                var state = jQuery('.validateState select').val();
                var stateFlag = states.indexOf(state) !== -1
                if (stateFlag) {

                    stateInvalid();

                    return false;
                } else {
                    var caseType = jQuery('.validateCase select').val();
                    var stateFlag2 = noWCCases.indexOf(state) !== -1
                    if (caseType == 'Workers Compensation' && stateFlag2) {

                        caseInvalid();

                        return false;
                    }
                }

                allValid();

            });

            jQuery('.validateCase select').on('change', function() {
                var state = jQuery('.validateState select').val();
                var stateFlag = states.indexOf(state) !== -1
                if (stateFlag) {

                    stateInvalid();

                    return false;
                } else {
                    var caseType = jQuery('.validateCase select').val();
                    var stateFlag2 = noWCCases.indexOf(state) !== -1

                    if (caseType == 'Workers Compensation' && stateFlag2) {
                        caseInvalid();

                        return false;
                    }


                    if (caseType == 'Social Security/Disablilty') {

                        caseInvalidss();

                        return false;
                    }
                }

                allValid();

            });

        });
    </script>
