    <script type="text/javascript">
        // COOKIE SCRIPT
        function formatTime(date) {
            var d = new Date(date),
                hours = '' + d.getHours(),
                minutes = '' + d.getMinutes(),
                seconds = d.getSeconds();
            return [hours, minutes, seconds].join(':');
        }

        function formatDate(date) {
            var d = new Date(date),
                month = '' + (d.getMonth() + 1),
                day = '' + d.getDate(),
                year = d.getFullYear();

            if (month.length < 2) month = '0' + month;
            if (day.length < 2) day = '0' + day;

            return [month, day, year].join('/');
        }

        function oasis_getCookie(cname) {
            var name = cname + "=";
            var decodedCookie = decodeURIComponent(document.cookie);
            var ca = decodedCookie.split(';');
            for (var i = 0; i < ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0) == ' ') {
                    c = c.substring(1);
                }
                if (c.indexOf(name) == 0) {
                    return c.substring(name.length, c.length);
                }
            }
            return "";
        }

        function oasis_setCookie(cname, cvalue, exdays) {
            var d = new Date();
            d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
            var expires = "expires=" + d.toUTCString();
            document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
        }

        function oasis_checkCookieInitialHitDate() {
            var InitialHitTime = oasis_getCookie("InitialHitDate");
            if (InitialHitTime != "" && InitialHitTime != null) {} else {
                var InitialHitTimeVal = new Date().getTime();
                InitialHitTimeVal = Math.round(InitialHitTimeVal * .001);
                oasis_setCookie("InitialHitDate", InitialHitTimeVal, 365);
            }
        }

        function oasis_checkCookieInitialHitTime() {
            var InitialHitTime = oasis_getCookie("InitialHitTime");
            if (InitialHitTime != "" && InitialHitTime != null) {} else {
                var InitialHitTimeVal = new Date().getTime();
                InitialHitTimeVal = Math.round(InitialHitTimeVal * .001);
                oasis_setCookie("InitialHitTime", InitialHitTimeVal, 365);
            }
        }

        function oasis_checkCookie_UTMs(param) {
            var urlParams = new URLSearchParams(window.location.search);
            //console.log(urlParams);
            var cookieval = urlParams.get(param);
            //console.log(cookieval);
            if (cookieval !== '' && cookieval !== null) {
                oasis_setCookie('oasis' + param, cookieval, 365);
            }
        }


        var UTMs = ['utm_campaign', 'utm_medium', 'utm_content', 'utm_term', 'utm_source', 'MatchType'];

        UTMs.forEach(function(utm) {
            oasis_checkCookie_UTMs(utm);
        });


        oasis_checkCookieInitialHitDate();
        oasis_checkCookieInitialHitTime();
